import React from 'react'

class App extends React.Component{
    constructor(){
        super()
        this.state = {
            fName: '',
            lname: '',
            name: '',
            placeOfBirth: '',
            phone: '',
            favoriteFood: '',
            email: '',
            aboutYourself: '',
            badges: []
        }
    }


    handleChange = (event) => {
        this.setState({
            [event.target.name]: event.target.value
        })
    }

    handleSubmit = (event) => {
        event.preventDefault()
        const person = {
            fullName: this.state.fName + ' ' + this.state.lName,
            placeOfBirth: this.state.placeOfBirth,
            email: this.state.email,
            phone: this.state.phone,
            favoriteFood: this.state.favoriteFood,
            aboutYourself: this.state.aboutYourself
        }
        this.setState(prevState => {
            badges
        })
    }

    render(){
        return(
            <div className='app-container'>
                <form className='form-container' onSubmit={this.handleSubmit}>
                    <input className='fName' placeholder='First Name' name='fName' value={this.state.fName} onChange={this.handleChange}></input>
                    <input className='lName' placeholder='Last Name' name='lName' value={this.state.lName} onChange={this.handleChange}></input>
                    <input className='email' placeholder='email' name='email' value={this.state.email} onChange={this.handleChange}></input>
                    <input className='place-of-birth' placeholder='Place of Birth' name='placeOfBirth' value={this.state.placeOfBirth} onChange={this.handleChange}></input>
                    <input className='phone' placeholder='Phone Number' name='phone' value={this.state.phone} onChange={this.handleChange}></input>
                    <input className='favorite-food' placeholder='Favorite Food' name='favoriteFood' value={this.state.favoriteFood} onChange={this.handleChange}></input>
                    <input className='about-yourself' placeholder='Tell us about yourself . . .' name='aboutYourself' value={this.state.aboutYourself} onChange={this.handleChange}></input>
                </form>
            </div>
        )
    }
}

export default App
